Method.Implementation:
set proxy = ##super(params) set proxy.athlete = ##class(%ZEN.proxyObject).%New() set proxy.athlete.key = ..AthleteKey set proxy.athlete.gender = ..Gender set proxy.athlete.lastName = ..LastName set proxy.athlete.firstName = ..FirstName set proxy.athlete.dob = $ZD(..DOB, 3)
set proxy.athlete.otherInfo = ##class(%ZEN.proxyObject).%New() set proxy.athlete.otherInfo.country = ..Country.ConvertToProxyObject() set proxy.athlete.otherInfo.city = ..City set proxy.athlete.otherInfo.club = ..Club set proxy.athlete.otherInfo.mainTrainer = ..MainTrainer set proxy.athlete.otherInfo.otherTrainers = ..OtherTrainers
set proxy.athlete.competitionsCount = "-" set proxy.athlete.paymentsCount = "-" if $ISOBJECT(proxy.competition.tournament){ if (params.loadPrtObjCompetitionsCount){ &sql(SELECT COUNT(Competition) INTO :proxy.athlete.competitionsCount FROM Dance_Data.ParticipantSingleOther WHERE Competition->Tournament = :proxy.competition.tournament.id AND AthleteKey = :proxy.athlete.key) }
if (params.loadPrtObjPaymentsCount){ &sql(SELECT COUNT(ID) INTO :proxy.athlete.paymentsCount FROM Dance_Data.ParticipantSingleOther WHERE Competition->Tournament = :proxy.competition.tournament.id AND AthleteKey = :proxy.athlete.key AND TicketStatus = 'Paid') } }
quit proxy
