servicesModule.factory('CompetitionImportSrvc', function(RESTSrvc) {    
    return {
        getAll: function(data){
            return RESTSrvc.getPromise({method: 'POST', url: AppSettings.admin + '/competition/import', data: data});
        },
        createAll: function(data){
            return RESTSrvc.getPromise({method: 'POST', url: AppSettings.admin + '/competition/createall', data: data});
	    },
        update: function(data){
	        return RESTSrvc.getPromise({method: 'POST', url: AppSettings.admin + '/competition/imported', data: data});
	    },
        removeOrRestore: function(id){
            return RESTSrvc.getPromise({method: 'DELETE', url: AppSettings.admin + '/competition/imported/'+id+'/removeorrestore'});
        },
        deleteById: function(tournamentId, competitionId){
            return RESTSrvc.getPromise({method: 'DELETE', url: AppSettings.admin + '/tournament/' + tournamentId + '/competition/' + competitionId});
        }
    }
});
